    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
          try {
            Class.forName("com.microsoft.sqlserver.jdbc.SQLServerDriver"); 
       Connection con;
            con = DriverManager.getConnection("jdbc:sqlserver://localhost:1433;databaseName =Library Management System","ibrahim1","12345");
       Statement st;
            st = con.createStatement();
       
       String q="insert into Professor(Name , Faculty) values ('"+jTextField1.getText()+"','"+jTextField2.getText()+"')";
            st.executeUpdate(q);
            
       jTextField1.setText("");
       jTextField2.setText("");
       JOptionPane.showMessageDialog(rootPane, "Professor is Registerd");
    
       con.close(); 
       st.close();
        } // end of try
        catch(ClassNotFoundException | SQLException e)
        {
            System.out.print("e : " + e.toString());
        }// end of catch
    }//GEN-LAST:event_jButton1ActionPerformed
